import React, { useRef } from 'react';
import './Home.css'; // Ensure this CSS file exists
import image1 from '../assets/text.png';
import image2 from '../assets/image 7.png';
import image3 from '../assets/h4hPhoto1.jpg';
import image4 from '../assets/groupPic1.jpeg';
import image5 from '../assets/acmAcmwPhoto.jpeg';

const Home = () => {
  // Create refs for each section
  const eventsRef = useRef(null);
  const updatesRef = useRef(null);
  const aboutUsRef = useRef(null);

  // Scroll function to target section
  const scrollToSection = (ref) => {
    ref.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      {/* Button Section */}
      <div className="icons">
        <button className="eventsbutton" onClick={() => scrollToSection(eventsRef)}>
          Upcoming Events
        </button>
        <button className="updatesbutton" onClick={() => scrollToSection(updatesRef)}>
          Updates on ACM-W
        </button>
        <button className="aboutbutton" onClick={() => scrollToSection(aboutUsRef)}>
          About ACM-W
        </button>
      </div>

      {/* Event Section */}
      <div ref={eventsRef} className="upcomingeventbox">
        <h1>UPCOMING EVENTS</h1>
        <p>Git Workshop</p>
        <p>Wednesday, May 22nd, 2024</p>
        <p>Location: Online</p>
        <img src={image2} alt="upcoming event" className="upcomingimage" />
      </div>

      {/* Updates Section */}
      <div ref={updatesRef} className="updatebox">
        <h1>Updates on ACM-W</h1>
        <p>Thank you for coming to the Hack-at-thon</p>
        <img src={image2} alt="update" className="updateimage" />
      </div>

      {/* About Us Section */}
      <div ref={aboutUsRef} className="about-us">
        <h1>About ACM-W</h1>
        <div className="gallery">
          <img src={image3} alt="Event" />
          <div className="desc">Events and Activities</div>
        </div>
        <div className="gallery">
          <img src={image4} alt="Mission" />
          <div className="desc">Our Mission</div>
        </div>
        <div className="gallery">
          <img src={image5} alt="ACM" />
          <div className="desc">ACM SCU Chapter</div>
        </div>
      </div>
    </div>
  );
};

export default Home;
